let div = document.querySelector('h2');
div.innerHTML = div.textContent.replace(/\S/g, "<span class='letter' style='display: inline-block'>$&</span>");

let timeline = anime.timeline({autoplay: true,loop: true}).add({
    targets: 'h2 .letter',
    scale: [4,1],
    opacity: [0,1],
    easing: "easeOutExpo",
    duration: 1000,
    delay: (el, i) => 70*i,
    endDelay: 500
  });
